package com.aslua.languageopen class DefFormatter : Formatter {    override fun createAutoIndent(text: CharSequence?): Int {        return 1    }    override fun format(text: CharSequence?, width: Int): CharSequence? {        return text    }    companion object {        @JvmField		var indentChar: Char = '\t'        private var _theOne: DefFormatter? = null        @JvmStatic        open fun getInstance(): DefFormatter {            if (_theOne == null) {                _theOne = DefFormatter()            }            return _theOne!!        }    }}